html {
  height: auto;
  font-size: 14px;
  font-weight: 400;
  line-height: 20px;
  background-color: #c5dfeb;
  box-sizing: border-box;
}

*::-moz-selection {
  color: white;
  background-color: rgba(185, 233, 212, 0.8);
}

*::selection {
  color: white;
  background-color: rgba(185, 233, 212, 0.8);
}

/*
  this is a recommended box-sizing reset to insure the box models use the intuitive border-box box model,
  while also allowing you to use content-box or padding-box on more specific selectors.
  https://css-tricks.com/box-sizing/
*/
*,
*:before,
*:after {
  box-sizing: inherit;
}

body {
  -webkit-perspective: 100vw;
  perspective: 100vw;
  min-height: 100vh;
  box-sizing: border-box;
  display: flex;
  position: relative;
  flex-direction: column;
  margin: 0;
  color: #555;
  padding: 0;
  font-family: Roboto, Helvetica, sans-serif;
  justify-content: space-between;
  background-color: #c5dfeb;
  background: linear-gradient(#c5dfeb 10%, #e2e7ea);
  line-height: 1;
  -webkit-font-smoothing: subpixel-antialiased;
  -moz-osx-font-smoothing: subpixel-antialiased;
}

p {
  line-height: 24px;
  font-size: 16px;
  font-weight: 100;
}

section {
  position: relative;
  box-sizing: border-box;
}

.subtle-text {
  color: rgba(255, 255, 255, 0.75);
}

.grid {
  position: absolute;
  right: -20%;
  bottom: 0;
  left: -20%;
  height: 256px;
  -webkit-transform: rotateX(70deg);
  transform: rotateX(70deg);
  -webkit-transform-origin: center bottom;
  transform-origin: center bottom;
  -webkit-animation: grid 1s linear infinite;
  animation: grid 1s linear infinite;
  background-image: linear-gradient(#333 0px, #333 1px, transparent 2px),
    linear-gradient(90deg, #333 0px, #333 1px, transparent 2px);
  background-position: center 0;
  background-size: 24px 24px;
}

.svg.epfl-logo {
  padding: 3px;
  width: 7em;
  margin: 0;
  transition: all 0.3s ease;
}

.call-to-action {
  font-size: 1em;
  background-size: 1px 100px;
}

.introducing {
  background-color: #ffffff1f;
  padding: 1em;
  border-radius: 2px;
  font-size: 16px;
  width: auto;
  display: inline-block;
  color: white;
  transition: all 150ms ease-out;
}
.introducing .new {
  padding: 8px;
  border-radius: 2px;
  background-color: #c7eddd;
  background-image: linear-gradient(45deg, #bfeeff 0%, #c7eddd 74%);
  margin-right: 1em;
  color: #1b75ba;
  box-shadow: -2px 3px 1px #00000033;
  text-shadow: -1px 1px 1px #00000033;
}
.introducing:hover {
  background-color: transparent;
}

.buttons-container {
  display: flex;
  margin-top: 1em;
  align-items: center;
}
.buttons-container .sdk-buttons {
  padding: 0 1em;
}
.buttons-container .sdk-buttons button {
  margin: 0 1em;
  background-image: none;
  background-color: transparent;
}
.buttons-container .sdk-buttons button:hover {
  color: #555;
  background-color: #c7eddd;
}

.not-subtle {
  position: absolute;
  top: 6%;
  height: 500px;
  width: 500px;
}
.not-subtle img {
  width: 100%;
  height: 100%;
}

.wrapper {
  background-color: white;
  margin: 0 auto;
  max-width: 1366px;
  overflow: hidden;
  position: relative;
  width: 100%;
}

.not-subtle {
  margin: 4em;
}

h1 em {
  font-weight: 400;
  font-style: initial;
  /* color: #bfeeff; */
}

.gradient {
  background-image: radial-gradient(
    ellipse at center -50%,
    #18334f 0%,
    #18334f 18%,
    #192532 57%,
    #192532 100%
  );
  color: white;
  padding: 4em;
}

.gradient h1,
.gradient h2 {
  color: white;
}

.links a {
  color: #8de2ff;
}

a,
a:active,
a:focus {
  text-decoration: none;
}

.links a:hover {
  text-decoration: underline;
}

.container {
