}

audio,
canvas,
iframe,
img,
svg,
video {
  vertical-align: middle;
}

.regular-box {
  margin: 1em 0;
  padding: 1em;
  background: #f7f9fa;
  box-shadow: 0px 1px 2px 0px rgba(0, 0, 0, 0.1);
  box-sizing: border-box;
  border-radius: 2px;
  display: flex;
  justify-content: space-between;
}

.bordered-box {
  position: relative;
  background-color: #fff;
  box-shadow: 0px 1px 2px 0px rgba(0, 0, 0, 0.1);
  display: block;
  padding: 24px;
}

p {
  word-spacing: 0.05em;
}

article {
  margin: 2em auto;
}

header {
  box-sizing: border-box;
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: 60px;
  background-color: transparent;
  position: absolute;
  width: 100%;
