  padding: 0.5em;
}

header img {
  height: 2em;
  width: 2em;
}

.header-logo {
  margin-right: 0.5em;
  border-radius: 50%;
  background-color: white;
  padding: 0.5em;
}

header h1 {
  font-size: 1.5em;
  color: #8de2ff;
  letter-spacing: 1px;
  font-weight: 100;
}

.slant-top-reverse {
  position: relative;
  /* show blue slant above canvas */
  z-index: 1;
}

.slant-top-reverse::before {
  top: 0;
  -webkit-transform: skewY(-1.5deg);
  transform: skewY(-1.5deg);
  -webkit-transform-origin: 0 0;
  transform-origin: 0 0;
}

.fold .angle {
  background-color: #bfeeff;
  position: absolute;
  width: 100%;
  height: 100%;
}

.slant-top-reverse::before {
  background: inherit;
  background-color: #bfeeff;
  content: "";
  display: block;
  height: 50%;
  left: 0;
  position: absolute;
  right: 0;
  z-index: -1;
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
}

.slant-bottom {
  -webkit-clip-path: polygon(0 10%, 100% 0%, 100% 70%, 0 100%);
  clip-path: polygon(0 10%, 100% 0%, 100% 70%, 0 100%);
}

section {
  padding: 2em;
}

.hero {
  /* background-color: #f8ceec; */
  background-color: #1f3957;
  background-image: radial-gradient(
    ellipse at center -50%,
    #18334f 0%,
    #18334f 18%,
    #192532 57%,
    #192532 100%
  );
  padding-bottom: 14rem;
  padding-top: 10rem;
  position: relative;
  width: 100%;
  height: 100%;
  color: white;
