.get-started {
  width: 100%;
  text-align: center;
}

canvas.lines {
  opacity: 0;
  position: absolute;
  width: 100%;
  height: 100%;
  left: 0;
  top: 0;
  z-index: 1;
  -webkit-animation: fadeInAlmost 1s ease-in;
  animation: fadeInAlmost 1s ease-in;
  -webkit-animation-fill-mode: forwards;
  animation-fill-mode: forwards;
}

.hero canvas.lines {
  -webkit-animation: fadeInBarely 1s ease-in;
  animation: fadeInBarely 1s ease-in;
  -webkit-animation-fill-mode: forwards;
  animation-fill-mode: forwards;
