img[alt="EPFL"] {
  -webkit-filter: invert(90%);
  filter: invert(90%);
}

img[alt="blue Brain Project logo"] {
  width: 10em;
  margin-top: 2em;
}

.icon {
  width: 20px;
}

section.config {
  display: none;
  position: absolute;
  height: 200px;
  width: 300px;
  padding: 25px;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  margin: auto;
}

.config div {
  margin-bottom: 20px;
}

.config input:not([type="submit"]) {
  height: 40px;
  width: 150px;
  font-size: 12px;
  border: none;
  background-color: #80808014;
  border-radius: 5px;
  padding: 4px 1em;
}

.config input[type="submit"] {
  width: 100px;
  height: 40px;
  font-size: 16px;
  display: block;
  margin: 5px auto;
  background: #274b76;
  color: #ffffff;
  border: none;
}

.config.opened {
  display: block;
}

.overlay {
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 1000;
  padding: 1.2rem;
  display: none;
  position: fixed;
  overflow-y: auto;
  overflow-x: hidden;
  background: rgba(0, 0, 0, 0.75);
  align-items: flex-start;
  justify-content: center;
}

.apps-list {
  position: absolute;
  right: 40px;
  top: 20px;
  display: none;
  background-color: #2d2f38f0;
  list-style: none;
  padding: 0;
  margin: 0;
  padding-top: 20px;
}

.apps-list:hover {
  display: block;
}

.apps-list li {
  height: 40px;
}

.property {
  padding: 0;
  display: flex;
  width: 100%;
}

.property .container {
  padding: 0.5em;
  display: flex;
  padding-left: 0;
  width: 100%;
  justify-content: start;
  flex-direction: row;
}

.property .caption {
  text-align: center;
}

.property .container div:not(:first-child):not(:nth-child(2)) {
  margin-left: 0.5em;
}

.property .category-icon {
  font-size: 1.5em;
  color: rgba(185, 233, 212, 0.8);
}

.handle {
  width: 0.5em;
  align-self: stretch;
  margin: -1px;
  margin-right: 1em;
  background-color: rgba(185, 233, 212, 0.8);
}

.property .container .key {
  color: #333;
  font-weight: 500;
  margin-left: 0.5em;
}

.property .value {
  font-weight: lighter;
  color: grey;
  margin-left: 0.5em;
}

.property .value i {
  margin: 0 0.5em;
}

.property .label {
  font-weight: lighter;
  color: grey;
  margin-left: 0.5em;
  font-style: italic;
}

.svg {
  transition: all 500ms ease-in;
  opacity: 0;
  height: 2em;
  width: 2em;
}

.svg.replaced-svg {
  opacity: 1;
}

.dashboard svg.svg path {
  fill: rgba(185, 233, 212, 0.8);
}

.property .value.ok {
  color: #b9e9d4;
}

.property .status {
  flex-grow: 1;
  text-align: right;
  font-size: 1.5em;
}

.statuses .handle {
  background-color: #6eddff66;
}

.show-off {
  height: 6em;
  width: 6em;
}

.dev-links {
  margin-top: 2em;
}

.text-center {
  text-align: center;
}

footer form {
  padding: 1em 0;
}

.input {
  margin: 1em 0;
}

.input label {
  display: block;
}

.input input {
  padding: 1em;
  margin-top: 1em;
  border: none;
  transition: all 100ms ease-in;
  border-radius: 4px;
  color: grey;
}

.input input:active,
.input input:focus {
  outline: none;
  background-color: #8de2ff57;
}

button {
  cursor: pointer;
  padding: 1em;
  -webkit-appearance: none;
  border: none;
  border-radius: 2px;
  color: grey;
  transition: all 300ms ease-in;
  background-color: #44c7f4;
  background-image: linear-gradient(45deg, #1b75ba 0%, #44c7f4 74%);
  background-size: 1px 50px;
  color: white;
  margin-top: 0;
}

button:active,
button:focus {
  outline: none;
}

button:hover {
  background-position: 50px;
}

.feature-description {
  padding: 0 2em;
  justify-content: flex-start;
  width: 40%;
}

.feature .feature-description p {
  flex-grow: 1;
}

.feature-list {
  justify-content: flex-start;
}

.feature {
  max-width: 250px;
  margin: 0 0.5em;
  justify-content: flex-start;
}

.feature h3 {
  color: #555;
  display: flex;
  font-size: 1.2em;
  line-height: 24px;
  height: 2em;
  margin-bottom: 0.2em;
}

.feature h3 svg {
  margin-right: 0.5em;
}

.feature h3 svg path {
  fill: #0000008a;
}

.feature h3 * {
  align-self: center;
}

section * {
  /* necessary to have text visible and create the slanting effect */
  z-index: 1;
}

section .highlight {
  margin: 4em;
  padding: 4em;
  position: relative;
  /* color: white; */
  background-color: #fafbfc;
}

.tab-menu {
  margin-bottom: 2em;
}

ul.menu {
  position: relative;
  padding: 0;
  display: flex;
  width: 50%;
  margin: 0 auto;
  box-sizing: border-box;
  justify-content: space-around;
}

.wee {
  height: 2px;
  width: 100%;
  background-color: #002b75;
  position: absolute;
  left: 0;
  bottom: 0;
  transition: 0.5s;
}

.menu-item {
  display: inline-block;
  padding: 0.25rem 0.25rem 0.5rem 0.25rem;
  margin: 0 0.25rem;
  color: #232323;
  font-size: 1.5rem;
  transition: 0.5s;
}

.menu-item button {
  font-size: 1rem;
  background: #44c7f4;
}

.current-menu-item button {
  background: #c5dfeb;
}

.fold {
  height: 100%;
  width: 100%;
  position: absolute;
  left: 0;
  top: 0;
  background-color: #fafbfc;
  -webkit-clip-path: polygon(0 10%, 100% 0%, 100% 70%, 0 100%);
  clip-path: polygon(0 10%, 100% 0%, 100% 70%, 0 100%);
}

.tick {
  height: 1em;
  width: 1em;
  display: inline-block;
  background-image: linear-gradient(45deg, #1b75ba 0%, #44c7f4 74%);
  -webkit-clip-path: polygon(0 0, 30% 0, 100% 100%, 70% 100%);
  clip-path: polygon(0 0, 30% 0, 100% 100%, 70% 100%);
}

.feature-description h2 {
  border-bottom: 1px solid #dbe3e7;
}

.slide-in {
  opacity: 0;
  position: relative;
  -webkit-animation-name: slideIn;
  animation-name: slideIn;
  -webkit-animation-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  animation-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
  -webkit-animation-iteration-count: 1;
  animation-iteration-count: 1;
  -webkit-animation-delay: 0.2s;
  animation-delay: 0.2s;
  -webkit-animation-duration: 0.7s;
  animation-duration: 0.7s;
  -webkit-animation-fill-mode: forwards;
  animation-fill-mode: forwards;
}

.slide-in.first {
  -webkit-animation-delay: 0s;
  animation-delay: 0s;
}

.card {
  margin: 2em;
  max-width: 400px;
}

.card .bordered-box {
  flex-grow: 1;
}

.card svg {
  font-size: 1.2em;
  margin-right: 1em;
}

.card ul {
  text-align: left;
  padding: 1em 2em 2em;
  margin: 0;
  line-height: 24px;
  font-size: 16px;
  font-weight: 100;
  width: 230px;
}

.card ul li {
  list-style: none;
  margin-bottom: 0.5em;
}

.card ul li:before {
  background-image: url("../img/check-circle.svg");
  content: "";
  background-position: center;
  height: 24px;
  width: 20px;
  margin-left: -28px;
