.wrap {
  flex-wrap: wrap;
}

.roles {
  max-width: 60%;
  margin: 0 auto;
}

.roles svg path,
.roles svg circle,
.roles svg rect {
  fill: #b9e9d4;
}

.container.contained {
  max-width: 1340px;
  padding-left: 20px;
  padding-right: 20px;
  margin-left: auto;
  margin-right: auto;
}

#what-about-it {
  margin-top: -5em;
  background-color: #2d2f38f0;
  background-image: radial-gradient(
    ellipse at center -70%,
    #18334f 0%,
