  line-height: 2em;
}

#what-about-it .container {
  margin: 0 2em;
  justify-content: center;
}

#what-about-it .container p:first-child {
  margin-top: 0;
}

#what-about-it h2 {
  color: white;
  margin-top: 0;
}

#roles {
  padding-bottom: 8em;
}

.section-title {
  color: #b3b8bd;
  width: 600px;
  line-height: 24px;
  font-weight: 400;
  text-align: center;
  margin: 0 auto;
}

.section-title h2 {
  font-size: 34px;
}

.section-title h3 {
  font-weight: 400;
}

#features .regular-box {
  padding: 2em;
}

.emph {
  display: inline-block;
  position: relative;
  width: 200px;
  text-align: left;
  padding: 0 8px;
}

.emph.in::before {
  -webkit-transform: scale3d(1, 1, 1);
  transform: scale3d(1, 1, 1);
  opacity: 0.5;
}

.emph::before {
  background: #b9e9d4;
  content: "";
  position: absolute;
  z-index: -1;
  bottom: 0;
  left: 0;
  width: 80%;
  height: 50%;
  opacity: 0.3;
  -webkit-transform: scale3d(0, 1, 1);
  transform: scale3d(0, 1, 1);
  -webkit-transform-origin: 0% 50%;
  transform-origin: 0% 50%;
  transition: -webkit-transform 0.5s;
  transition: transform 0.5s;
  transition: transform 0.5s, -webkit-transform 0.5s;
  transition-timing-function: cubic-bezier(0.2, 1, 0.3, 1);
}

@-webkit-keyframes emphasis {
  0% {
    opacity: 0;
  }
  20% {
    opacity: 1;
  }
  80% {
    opacity: 1;
  }
  100% {
    opacity: 0;
  }
}

@keyframes emphasis {
  0% {
    opacity: 0;
  }
  20% {
    opacity: 1;
  }
  80% {
    opacity: 1;
  }
  100% {
    opacity: 0;
  }
}

@-webkit-keyframes fadeInAlmost {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 0.2;
  }
}

@keyframes fadeInAlmost {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 0.2;
  }
}

@-webkit-keyframes fadeInBarely {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 0.2;
  }
}

@keyframes fadeInBarely {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 0.2;
  }
}

@-webkit-keyframes slideIn {
  0% {
    opacity: 0;
    -webkit-transform: translateY(40px);
    transform: translateY(40px);
  }
  100% {
    opacity: 1;
    -webkit-transform: translateY(0);
    transform: translateY(0);
  }
}

@keyframes slideIn {
  0% {
    opacity: 0;
    -webkit-transform: translateY(40px);
    transform: translateY(40px);
  }
  100% {
    opacity: 1;
    -webkit-transform: translateY(0);
    transform: translateY(0);
  }
}

@-webkit-keyframes slideIn1 {
  0% {
    opacity: 0;
    -webkit-transform: translateY(40px);
    transform: translateY(40px) rotateY(90deg);
  }
  100% {
    opacity: 1;
    -webkit-transform: translateY(0);
    transform: translateY(0) rotateY(0deg);
  }
}

@keyframes slideIn1 {
  0% {
    opacity: 0;
    -webkit-transform: translateY(40px);
    transform: translateY(40px) rotateY(90deg);
  }
  100% {
    opacity: 1;
    -webkit-transform: translateY(0);
    transform: translateY(0) rotateY(0deg);
  }
}

