    width: 100%;
    height: 200px;
    width: 200px;
    margin: 0 auto;
  }
  header {
    padding: 0 1em;
  }
  header img {
    height: 1.5em;
    width: 1.5em;
  }
  header h1 {
    font-size: 1em;
  }
  .menu-block a {
    padding: 0;
  }
  .hero .center {
    box-sizing: border-box;
    width: 100%;
    padding: 0 1rem;
  }
  .hero h1 {
    font-size: 1.5em;
  }
  .hero h2 {
    font-size: 1em;
  }
  .call-to-action {
    font-size: 1em;
  }
  section {
    padding: 0;
  }
  .container.contained {
    max-width: initial;
    width: 100%;
    padding: 0;
    margin: 0;
  }
  .section-title {
    width: 100%;
    padding: 1em;
    box-sizing: border-box;
  }
  .section-title h2 {
    font-size: 1.2em;
  }
  #features .regular-box {
    padding: 0;
  }
  ul.menu {
    margin-top: 0.5em;
  }
  .menu-item {
    margin: 0;
  }
  .menu-item button {
    font-size: 0.5em;
  }
  .tab-menu {
    margin-bottom: 1em;
  }
  .container {
    flex-wrap: wrap;
  }
  .feature-description {
    width: 100%;
    padding: 1em;
  }
  .feature-list {
    padding: 1em;
  }
  .feature {
    max-width: initial;
  }
  .container {
    width: 100%;
    max-width: initial;
  }
  .container.roles {
    width: 100%;
    max-width: initial;
  }
  .card {
    width: 100%;
    max-width: initial;
    margin: 0;
    padding: 1em;
  }
  .handle {
    padding: 2px;
  }
  #what-about-it {
    padding: 2em;
  }
  #what-about-it .container {
