  }
  .show-off {
    height: 4em;
    width: 4em;
  }
  .bot-links .container.column {
    padding-bottom: 2em;
  }
  footer nav {
    width: 100%;
    padding: 2em;
  }
  .subscribe {
    padding: 2em;
  }
  .container.links {
    padding: 2em 0 0;
  }
}
