const htmlnano = require('./index');
const fs = require('fs');
const safePreset = require('./lib/presets/safe').default;

const options = {
    minifySvg: safePreset.minifySvg,
};

console.log(options);

const svg = fs.readFileSync('./orig.html', 'utf8');

htmlnano
    .process(svg, options, {})
    .then(function (result) {
        console.log(result.html);
    })
    .catch(function (err) {
        console.error(err);
    });
